        private void button1_Click(object sender, EventArgs e)
        {
            string nombre;
            double sueldo_base, sueldo_neto, comision, renta, ventas;
            try
            {
                nombre = textBox1.Text;
                sueldo_base = Convert.ToDouble(textBox2.Text);
                ventas = Convert.ToDouble(textBox3.Text);
                comision = ventas * 0.20;
                renta = (sueldo_base + comision) * 0.10;
                sueldo_neto = sueldo_base + comision - renta;
                textBox4.Text = Convert.ToString(comision);
                textBox5.Text = Convert.ToString(renta);
                textBox6.Text = Convert.ToString(sueldo_neto);
            }
            catch
            {
                MessageBox.Show("Datos no válidos");
            }
        }

        private void button2_Click(object sender, EventArgs e)
